<script setup lang="ts">
import { onMounted, onUnmounted, ref, watch } from "vue"
import axios from "axios"
import { stringify } from "qs"
import { useMainStore } from "../../store"
import { storeToRefs } from "pinia"

onMounted(() => {
  window.addEventListener("keydown", (e: any) => {
    if (e.key === "Enter" && isFocus.value) {
      login()
    }
  })
})
onUnmounted(() => {
  window.removeEventListener("keydown", (e: any) => {
    if (e.key === "Enter" && isFocus.value) {
      login()
    }
  })
})

const store = useMainStore()
const { isLover } = storeToRefs(store)
const wj = ref()
const nickname = ref("")
const cipher = ref("")

const login = () => {
  axios
    .post(
      "http://43.139.36.99:3333/zdf/login",
      stringify({
        nickname: nickname.value,
        cipher: cipher.value
      })
    )
    .then((res) => {
      if (res.data.status == 0) {
        store.isLover = true
        localStorage.setItem("canse", "77c143848")
      }
    })
    .catch((err) => {
      console.log(err)
    })
}

const isFocus = ref<boolean>(false)
const focus_handle = () => {
  isFocus.value = true
}
const blur_handle = () => {
  isFocus.value = false
}
